import {useState} from 'react'
import { createRoot } from 'react-dom/client'
let num = 0
function HandleClick({count ,handleIncrease,handleDecrease}) {
  return (
    <>
        <button onClick={handleIncrease} type="button">Increasement</button>
        <button onClick={handleDecrease} type="button">decreasement</button>
    </>
  )}

  

function Head() {
    const [count, setCount] = useState(0)
    const [showHandleClick , setShowHandleClick] = useState(false)

    function handleIncrease() {
      setCount(count + 1)
    }
  
    function handleDecrease() {
      setCount(count - 1)
    }

    function handleButtonClick() {
      setShowHandleClick(true)
    }

return (
  <>
     <main className="head-container">
      <form>
          <input id='input-field'  placeholder="e.g. oregano" type="text" aria-label="Add ingradient" name='ingredient'  />

          {/* type = button to avoid changing url submiting when we click the button */}
          <button onClick={handleButtonClick}type= "button">{count}</button>
      </form>
     </main>

     <form>
       <ul>

       </ul>
     </form>
     {showHandleClick && <HandleClick
      count= {count}
      handleIncrease = {handleIncrease}
      handleDecrease = {handleDecrease}
     />}
   </>

)

}
  



export default Head
