import React, { useState } from 'react';

// move all the functions and [count]
function HandleClick({ count, handleIncrease, handleDecrease }) {
  return (
    <>
      <button onClick={handleIncrease} type="button">Increasement</button>
      <button onClick={handleDecrease} type="button">Decreasement</button>
    </>
  );
}

// move all these into head to make count 
function Head() {
  const [count, setCount] = useState(0);
  const [showHandleClick, setShowHandleClick] = useState(false);   // new thing i learned

  function handleIncrease() {
    setCount(count + 1);
  }

  function handleDecrease() {
    setCount(count - 1);
  }

  function handleButtonClick() {
    setShowHandleClick(true);
  }

  return (
    <>
      <main className="head-container">
        <form>
          <input
            id='input-field'
            placeholder="e.g. oregano"
            type="text"
            aria-label="Add ingredient"
            name='ingredient'
          />
          {/* type = button to avoid changing URL when submitting */}
          <button onClick={handleButtonClick} type="button">{count}</button>
        </form>
      </main>

      <form>
        <ul></ul>
      </form>
      
      {showHandleClick && (
        <HandleClick
          count={count}
          handleIncrease={handleIncrease}
          handleDecrease={handleDecrease}
        />
      )}
    </>
  );
}

// Render the component to the DOM
export default Head;
