import { useState } from 'react'
import '../index.css'

function Head() {
  const [ inputValue , setInputValue ] = useState('')
  const [ list , setList ] = useState([])

  function Render() {
    list.map((item) => {
      return <li>{item}</li>
    })
  }

  function handleInputValue(event) {
    setInputValue(event.target.value)
  }
  function handleClick() {
      console.log(inputValue)
      setInputValue('')
      setList(prev => [...prev, inputValue])
      console.log(list) // still not updated because setList is  asynchronous
  }

  return (
    <>
      <form className="head-container">
        <input value = {inputValue} onChange = {handleInputValue} ></input>
        <button type = "button" onClick= {handleClick} >Add ingredient</button>
      </form>
      <form>
        <ul>
           <li>{Render}</li>
        </ul>
      </form>
    </>
  )
}


export default Head
