const list = ['first ingrdient'];
function handleSubmit(event) {
        // to avoid changing the url when you submit
        event.preventDefault()
        const formData = new FormData(event.currentTarget)
        const newIngredient = formData.get("ingredient")
        list.push(newIngredient)
        console.log(list)
        const map1 = list.map(element=> <li>{element}</li>)
        console.log(map1)


}
function Head() {

    return (
    <>
       <main className="head-container">
        <form onSubmit={handleSubmit}>
            <input id='input-field'  placeholder="e.g. oregano" type="text" aria-label="Add ingradient" name='ingredient'  />

            {/* type = button to avoid changing url submiting when we click the button */}
            <button type= "button">+ Add ingredient</button>
        </form>
       </main>

       <form>
         <ul>
            <handleSubmit />
         </ul>
       </form>
     </>
    )
}



export default Head
