import {useState} from 'react'
function HandleClick() {
  const [count, setCount] = useState(0)

  function handleIncrease() {
    setCount(count + 1)
  }
  function handleDecrease() {
    setCount(count - 1)
  }
  return (
    <>
      <p>Count: {count}</p>
      <button onClick={handleIncrease} type="button">Increasement</button>
      <button onClick={handleDecrease} type="button">decreasement</button>
    </>
  )
}



function Head() { 
  const [showHandleClick,setShowHandleClick] = useState(false)
  const [ list , setList ] = useState([])
  const [ inputValue, setInputValue ] = useState('')
  const [ placeHolder, setPlaceHolder ] = useState('e.g. oregano')

  function handleButtonClick() {
    if(inputValue !== '') {
    setList(prevList => [...prevList, inputValue])
    setInputValue('')
    } else {
      setPlaceHolder('Please enter something')
    }
    setShowHandleClick((prev) => !prev) // appearance and disappearance
  }

  function handleInputValue(event) {
    setInputValue(event.target.value)
  }
  

    return (
    <>
       <main className="head-container">
        <form>
            <input 
            id='input-field'   
            placeholder={placeHolder}
            value = {inputValue}
            onChange = {handleInputValue}
            type="text" 
            aria-label="Add ingradient" name='ingredient'  />

            {/* type = button to avoid changing url submiting when we click the button */}
            <button onClick={handleButtonClick} type= "button">+ Add ingredient</button>
        </form>
       </main>

       <form>
         <ul>
         {list.map((item, index) => ( <li key={index}>{item}</li> ))}
         </ul>
       </form>
       {showHandleClick && <HandleClick
       />}
     </>

    )

  }

export default Head
